scripts.build:
mkdir -p public && cp index.html script.js styles.css public/ && ([ -f 404.html ] && cp 404.html public/) && if [ -z "$OPENWEATHERMAP_API_KEY" ]; then echo 'Error: OPENWEATHERMAP_API_KEY env var not set.' && exit 1; fi && echo "// Client-side environment variables
const ENV = {
    OPENWEATHERMAP_API_KEY: '${OPENWEATHERMAP_API_KEY}'
};

// Expose the environment to the window
window.ENV = ENV;" > public/env.js && echo 'Build finished.'
